mapper.insert:
insert into daily_record (id, content, disabled, 
  create_time, create_id, task_id
  )
values (#{id,jdbcType=VARCHAR}, #{content,jdbcType=VARCHAR}, #{disabled,jdbcType=BIT}, 
  #{createTime,jdbcType=TIMESTAMP}, #{createId,jdbcType=VARCHAR}, #{taskId,jdbcType=VARCHAR}
  )

mapper.select:
select id, content, disabled, create_time, create_id, task_id
from daily_record
where id = #{id,jdbcType=VARCHAR}
